import React from "react";
import "./Contact.css";
import Button from "@material-ui/core/Button";
import { TextValidator, ValidatorForm } from "react-material-ui-form-validator";
import Box from '@material-ui/core/Box';


export default class SomeComponent extends React.Component {

  state = {
    data: { email: "", phone: "", name: "" },
  };

  emailRef = React.createRef();
  phoneRef = React.createRef();
  nameRef = React.createRef();

  handleValue = (event) => {
    const { data } = this.state;
    data[event.target.name] = event.target.value;
    this.setState({ data });
  };

  handleBlur = (event) => {
    const { name, value } = event.target;
    if (name === "email") {
      // set true as second parameter to onBlur required validation
      this.emailRef.current.validate(value);
    }
  };

  handlephoneBlur = (event) => {
    const { number, value } = event.target;
    if (number === "phone") {
      // set true as second parameter to onBlur required validation
      this.phoneRef.current.validate(value);
    }
  };

  handleNameBlur = (event) => {
    const { name, value } = event.target;
    if (name === "name") {
      // set true as second parameter to onBlur required validation
      this.nameRef.current.validate(value);
    }
  };

  handleSubmit = () => {
    // your submit logic
  };

  render() {
    const { data } = this.state;
    return (
      <div className="contact">
        <img
          className="contact__background"
          src="https://images.unsplash.com/photo-1480506132288-68f7705954bd?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1586&q=80"
          alt=""
        />
        <Box>
        <ValidatorForm
          ref={(r) => (this.form = r)}
          onSubmit={this.handleSubmit}
          instantValidate={false}
        >
        <div className='contact__name'>
          <TextValidator
            ref={this.nameRef}
            name="name"
            label="name"
            value={data.name}
            onBlur={this.handlenameBlur}
            onChange={this.handleValue}
            validators={["isname", "required"]}
            errorMessages={["wrong name", "this field is required"]}
          />
          </div>
          <br />
          <div className='contact__email'>
          <TextValidator
            ref={this.emailRef}
            name="email"
            label="email"
            value={data.email}
            onBlur={this.handleBlur}
            onChange={this.handleValue}
            validators={["isEmail", "required"]}
            errorMessages={["wrong email", "this field is required"]}
          />
          </div>
          <br />
          <div className='contact__phone'>
          <TextValidator
            ref={this.phoneRef}
            name="phone"
            label="phone"
            value={data.phone}
            onBlur={this.handlephoneBlur}
            onChange={this.handleValue}
            validators={["isphone", "required"]}
            errorMessages={["wrong phone number", "this field is required"]}
          />
          </div>
          <br />
          <Button onClick={() => this.form.submit()}>submit</Button>
        </ValidatorForm>
        </Box>
      </div>
    );
  }
}
