import React from "react";
import { useForm } from "react-hook-form";
import "./Contact.css";


const required = "This field is required";
  const maxLength = "Your input exceed maximum length";
  const errorMessage = (error) => {
    return <div className="invalid-feedback">{error}</div>;
  };


export default function Contact() {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => console.log(data);
  console.log(errors);

  return (
    <div className="contact">
      <img
        className="contact__background"
        src="https://images.unsplash.com/photo-1480506132288-68f7705954bd?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1586&q=80"
        alt=""
      />
      <div className="container">
      <div className="col-sm-12">
        <h3>Contact Me</h3>
      </div>
      <div className="contact__form">
        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="form-group">
            <input
              className="form-control"
              type="text"
              placeholder="Name"
              ref={register("Name", { required: true, maxLength: 80 })}
            />

          </div>
          <div className="form-group">
            <input
              className="form-control"
              type="text"
              placeholder="Email"
              ref={register("Email", { required: true, pattern: /^\S+@\S+$/i })}
            />

          </div>
          <div className="form-group">
            <input
              className="form-control"
              type="tel"
              placeholder="Mobile Number"
              ref={register("Mobile number", {
                required: true,
                minLength: 6,
                maxLength: 12,
              })}
            />

          </div>
          <input type="submit" />
        </form>
      </div>
    </div>
    </div>
  );
}
